import React, { useRef } from "react";
import Header from "../components/Header/Header";
import OfferSection from "../components/OfferSection/OfferSection";
import RoomSection from "../components/RoomSection/RoomSection";
import PeopleCommentsSection from "../components/PeopleCommentsSection/PeopleCommentsSection";
import BookingSection from "../components/BookingSection/BookingSection";
import UpperTownSection from "../components/UpperTownSection/UpperTownSection";
import DiscountSection from "../components/DiscountSection/DiscountSection";
import StartSection from "../components/StartSection/StartSection";
import HomepageinnerSection from "../components/HomepageinnerSection/HomepageinnerSection";
import "./Homepage.css";

const Homepage = () => {
  const scrollToSection = (ref) => {
    ref.current.scrollIntoView({ behavior: "smooth" });
  };

  const offerSectionRef = useRef(null);
  const roomSectionRef = useRef(null);
  const peopleCommentsSectionRef = useRef(null);
  const bookingSectionRef = useRef(null);
  const upperTownSectionRef = useRef(null);
  const discountSectionRef = useRef(null);
  const homepageinnerSectionRef = useRef(null);

  return (
    <div className="homepage">
      <Header />
      <StartSection ref={offerSectionRef} />
      <OfferSection ref={offerSectionRef} />
      <RoomSection id="roomSection" ref={roomSectionRef} />
      <PeopleCommentsSection ref={peopleCommentsSectionRef} />
      <BookingSection ref={bookingSectionRef} />
      <UpperTownSection ref={upperTownSectionRef} />
      <DiscountSection ref={discountSectionRef} />
      <HomepageinnerSection ref={homepageinnerSectionRef} />
    </div>
  );
};

export default Homepage;
